sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

   if (isLoading || newValue === '') {
      return;
   }

   //If short description changes, create a warning when state is approving

	//alert("Current state is " + g_form.getValue('state'));

	if ( typeof onChange.changeFlag == 'undefined' ) {
        // It has not... perform the initialization
		alert('create change flag');
        onChange.changeFlag = 0;
	} else {
		if (onChange.changeFlag == 1) {
			alert('reset change flag');
			onChange.changeFlag = 0;
			return;
		}
	}

	var u_stateValue = g_form.getValue('state');
	var u_stateLabel = g_form.getOption('state',u_stateValue).text;
	alert("Current state is '" + u_stateLabel + "'");

	if (u_stateLabel == 'Awaiting Approval') {
		alert("The current state prevents you from changing the description. Changing it back to '" + oldValue + "'!");
		alert('set change flag');
		onChange.changeFlag = 1;
		g_form.setValue('short_description', oldValue);
	}

}
